ted.</h5>'),(1965,229,'_event_content','field_5a621095d626c'),(1966,229,'event_package',''),(1967,229,'_event_package','field_5a6222a9f43c7'),(1968,229,'event_passes_0_title','Passes'),(1969,229,'_event_passes_0_title','field_5a625f711c1ff'),(1970,229,'event_passes_0_price','$1,975'),(1971,229,'_event_passes_0_price','field_5a625fa91c200'),(1972,229,'event_passes_0_description',''),(1973,229,'_event_passes_0_description','field_5a625fc61c201'),(1974,229,'event_passes','1'),(1975,229,'_event_passes','field_5a625f5e1c1fe'),(1976,229,'event_venue','39'),(1977,229,'_event_venue','field_5a6224a7dde1d'),(1978,229,'event_start_date','20180503'),(1979,229,'_event_start_date','field_5a622aacf8cf9'),(1980,229,'event_start_time','19:30:00'),(1981,229,'_event_start_time','field_5a622acff8cfa'),(1982,229,'event_end_time',''),(1983,229,'_event_end_time','field_5a622b05f8cfb'),(1984,229,'event_participant_groups_0_header','Chef'),(1985,229,'_event_participant_groups_0_header','field_5a6224e6d31dd'),(1986,229,'event_participant_groups_0_participants','combo'),(1987,229,'_event_participant_groups_0_participants','field_5a6224fad31de'),(1988,229,'event_participant_groups','2'),(1989,229,'_event_participant_groups','field_5a6224cad31dc'),(1990,229,'event_banner',''),(1991,229,'_event_banner','field_5a6260de00b05'),(1992,229,'event_sidebar_image',''),(1993,229,'_event_sidebar_image','field_5a6260fa00b06'),(1994,229,'event_thumbnail','576'),(1995,229,'_event_thumbnail','field_5a69fb1602917'),(1996,229,'event_options_event_hide_guide_btn','1'),(1997,229,'_event_options_event_hide_guide_btn','field_5a625ed82515e'),(1998,229,'event_options_event_show_faq_btn','1'),(1999,229,'_event_options_event_show_faq_btn','field_5a625efa2515f'),(2000,229,'event_options',''),(2001,229,'_event_options','field_5a625eb82515d'),(2002,229,'_yoast_wpseo_content_score','30'),(2003,229,'event_participant_groups_0_chefs','a:1:{i:0;s:2:\"37\";}'),(2004,229,'_event_participant_groups_0_chefs','field_5a622c57db3a5'